void print_board() {
    cout << endl << "   a  b  c  d  e  f  g  h" << endl;
    for (int i = 0; i < 8; i++) {
        cout << i + 1 << "  ";
        for (int j = 0; j < 8; j++) {
            cout << board[i][j] << "  ";
        }
        cout << i + 1 << endl;
    }
    cout << "   a  b  c  d  e  f  g  h" << endl << endl;
}

// Function to check if a move is valid
bool is_valid_move(int from_x, int from_y, int to_x, int to_y) {
    // Check if the move is within the board limits
    if (from_x < 0 || from_x > 7 || from_y < 0 || from_y > 7 || to_x < 0 || to_x > 7 || to_y < 0 || to_y > 7) {
        return false;
    }
