1. bool Collider::checkCollision(Collider& other, float push)
{
	sf::Vector2f otherPostion = other.GetPostion();
	sf::Vector2f otherHalfSize = other.GetHalfSize();

	sf::Vector2f thisPostion = GetPostion();
	sf::Vector2f thisHalfSize = GetHalfSize();

	float deltaX = otherPostion.x - thisPostion.x;
	float deltaY = otherPostion.y - thisPostion.y;

	float intersectX = abs(deltaX) - (otherHalfSize.x + thisHalfSize.x);
	float intersectY = abs(deltaY) - (otherHalfSize.y + thisHalfSize.y);

	if (intersectX < 0.0f && intersectY < 0.0f)
	{
		push = std::min(std::max(push, 0.0f), 1.0f);

		if (intersectX > intersectY)
		{
			if (deltaX > 0.0f)
			{
				Move(intersectX * (1.0f - push), 0.0f);
				other.Move(-intersectX * (push), 0.0f);
			}
			else
			{
				Move(-intersectX * (1.0f - push), 0.0f);
				other.Move(intersectX * (push), 0.0f);
			}
		}
		else
		{
			if (deltaY > 0.0f)
			{
				Move(0.0f, intersectY * (1.0f - push));
				other.Move(0.0f, -intersectY * (push));
			}
			else
			{
				Move(0.0f, -intersectY * (1.0f - push));
				other.Move(0.0f, intersectY * (push));
			}
		}

		return true;
	}

	return false;
}	
