contract MyNFT is ERC721 {

  using Strings for uint256;
  using Base64 for bytes;

  string public uriPrefix = "ipfs://";
  string public uriSuffix = ".json";

  string[] public traits;

  mapping(uint => string) _tokenURIs;

  constructor(
    string[] memory _traits    
  ) ERC721("MyNFT", "NFT") {
    traits = _traits;
  }

  function mintNFT(address recipient) public returns (uint256) {
    uint256 newTokenId = _tokenIds.current();
    _mint(recipient, newTokenId);
    generateTokenURI(newTokenId);
    return newTokenId;
  }

  function generateTokenURI(uint256 _tokenId) internal {

    string memory attributes = _generateTokenAttributes(_tokenId);

    string memory json = Base64.encode(
      bytes(
        string(
          abi.encodePacked(
            '{"name": "My NFT #', _tokenId.toString(), '", "description": "A unique NFT!", ', attributes, '}'
          )
        )
      )
    );

    _tokenURIs[_tokenId] = string(
      abi.encodePacked(uriPrefix, json, uriSuffix) 
    );
  }

  function _generateTokenAttributes(uint256 _tokenId) internal view returns (string memory) {
    string[5] memory traits;

    traits[0] = string(abi.encodePacked("Color: ", traits[_tokenId % traits.length]));

    return string(abi.encodePacked('[', string(abi.encodePacked(traits)), ']'));
  }

  function tokenURI(uint256 _tokenId) public view override returns (string memory) {
    require(_exists(_tokenId), "ERC721Metadata: URI query for nonexistent token");
    return _tokenURIs[_tokenId];    
  }
}
```

